def getMinLength(seq: str) -> int:
    # Write your code here
    # hasRemoved as a bool flag set to true
    stack = []
    for char in seq:
        if char == "A" or len(stack) == 0:
            stack.append(char)
        else:
            stack.pop()

    return len(stack)

